   Public Sub New()
        MyBase.New()
        InitializeComponent()
        Dim sProvider as new EncryptedLicenseProvider()
        Dim sLicense as EncryptedLicense = sProvider.GetLicense(LICENSE_PARAMETERS, LICENSE_FILE)
        If sLicense is Nothing Then
            Dim myForm As New LicenseInstallForm
