This example demonstrates the typical usage of the EncryptedLicenseProvider to license a component or
control.  The component/control must first initialize the EncryptedLicenseProvider with the public 
key encryption parameters required to validate the license using .  
It can then use the .NET license manager to obtain a license for the current context 
(runtime or design).  If there is not a valid license the component designer can  
choose the appropriate course of action based on the context.  In this case the code uses the generic
 to prompt the user to install a license - but allows use of 
the component without a license for evaluation purposes. Applications created using the unlicensed evaluation 
component will display a nag MessageBox.

#using Infralution.Licensing;
#using System.ComponentModel;
[LicenseProvider(typeof(Infralution.Licensing.EncryptedLicenseProvider))]
public class MyControl : Control
{
   const string LICENSE_PARAMETERS = 
      @"

              z7ijwu7osE4HcGZH7+PhOYw3WuZp/+1szNEjrEo61WVO2sklsdVJpjShXPzUDjlDDvnIFZo4d0l8IFswCYleRS+5PVOkqH0OnAHOSIvoHMNxRyKi9klj7ZD55sKfDJu17MUsjLFLc741B8EvQ3dXPLQoTc+TF5mKOm6o8BnrSuU=
              AQAB

           NqPti5+oayvPPlaETb5gNi9u32sze0o9AhlVEtWm3rfo3gGV/jKXDqQBd9Icy9xgfrEOVKvCyrhnCUEowQDOBPaVMiDqdm9UBRM/KAQt4kUAb2bhl8W47R09bikvahfJWfP+zyic3oin61B1jvuI2CSYjiRi4a5Qz2hudDP5MxM=
           lL2tfh7eUgkZS+I0/yoRowAP7p++zXma4JgO/Npf0QXnbBy3pKw+B5U9jtfwydKleM22Wgk/KBG5uyYXWaJeG/Pe6I29uLD1s5uo+Y9EzVaXDdi9HMQwIIvrv7dcgZlPPkzmkSm1X3uqfHl9R4+NfP9noDBKiQBVSWkem/iH87E=
           7
        ";

    public MyControl()
    {
        InitializeComponent();
        EncryptedLicenseProvider.SetParameters(LICENSE_PARAMETERS);
        License license;
        if (!LicenseManager.IsValid(typeof(MyControl), this, out license))
        {
            if (LicenseManager.CurrentContext.UsageMode == LicenseUsageMode.Runtime)
            {
                MessageBox.Show("This application was created using an unlicensed version of MyControl", 
                                "Unlicensed Application");
            }
            else
            {
                LicenseInstallForm form = new LicenseInstallForm();
                form.ShowDialog("MyControl", "www.mycompany.com", typeof(MyControl)); 
            }
        }
    }
} 


Imports System.ComponentModel
Imports Infralution.Licensing
<LicenseProvider(GetType(Infralution.Licensing.EncryptedLicenseProvider))> _
Public Class MyControl
   Inherits System.Windows.Forms.UserControl

   Const LICENSE_PARAMETERS As String = _
       "<LicenseParameters>" & _
       "       <RSAKeyValue>" & _
       "       <Modulus>rzRJ6dZ4ZTJ8/EzFtq2XIaik7QBSc24b8i8KJrPaxXGiCMDFcj8JtEFePa1SYX1W3cQoiwvCAF/MYmkXto74WdApYzILel+bOHSaUXTImrxpPtykNhPD7fF3PEXxrLRrTjUWQyEX5+XSaNPstmbDJw/Zg83mraCfmGeLcsiEypM=</Modulus>" & _
       "       <Exponent>AQAB</Exponent>" & _
       "       </RSAKeyValue>" & _
       "       <DesignSignature>GJYsK2rQE7uj539+QCyJ3Z2GyrNEZQNxruxOQR6P2jo+Ze3Ev5Kgbf3N33RaLf7oEUsZNF4Gv7w5UFgYcdHzCphqznt4vcR++u9tb0sC5m6hf26538GXhkNodyVR9FDcv1AX4+PaM/kY+Z8mrMMmC148lYPpCHypXvgjOmDd9oQ=</DesignSignature>" & _
       "       <RuntimeSignature>coZIUnOTaRZCIa67HBMM6/xbkQNYS+l4ROeLmzn7SqRamBwP7cbfOEAsxlFV2roKlY6oz1B91/PYcTVPLfWahwjHaiAaLjKAosXK41yZOozu0vRWpv4H9ERiEli0pYX0M8fMBIkvSDT6n9X9T/6U4ujyefv8b4IxbYsB3zsjjx0=</RuntimeSignature>" & _
       "</LicenseParameters>"

   Public Sub New()
        MyBase.New()
        InitializeComponent()
        EncryptedLicenseProvider.SetParameters(LICENSE_PARAMETERS)
        Dim slicense as License
        If Not LicenseManager.IsValid(GetType(MyControl), Me, slicense) Then
            If LicenseManager.CurrentContext.UsageMode = LicenseUsageMode.Runtime Then
                MessageBox.Show("This application was created using an unlicensed version of MyControl", "Unlicensed Application")
            Else
                Dim myForm As New LicenseInstallForm
                myForm.ShowDialog("MyControl", "www.mycompany.com", GetType(MyControl))
            End If
        End If
   End Sub
End Class

example.code:
static void Main(string[] args)
{
 PDFDocument doc = new PDFDocument();

 // Load a PDF document
 doc.Load("input_doc1.pdf");

 // Handle the event where a new bookmark is
 // created in the merged document
 doc.MergeBookmarks +=
  new MergeBookmarksEventHandler(doc_MergeBookmarks);

 // Merge with another PDF document
 doc.Merge("input_doc2.pdf");

 doc.Save("MergeBookMarks.pdf");
 doc.Close();
}

// Event handler that will provide the text,
// color, and style for the bookmark that
// is created when the doucment is merged
// with another
private static void doc_MergeBookmarks(
          object sender,
          ref string title,
          ref Color color,
          ref PDFBookmarkStyle style,
          ref bool createRootBookmark)
{
 title = "Merged Bookmark";
 color = Color.Green;
 style = PDFBookmarkStyle.Bold;
 createRootBookmark = true;
}
